require 'simplecov'
SimpleCov.start 'rails' do
  add_filter '/spec/'
  add_filter '/features/'
  add_filter '/config/'
  add_filter '/db/'
  add_filter '/vendor/'
end

require 'bundler/setup'
# IMPORTANT: This file is generated by cucumber-rails - edit at your own peril.
